  source: { x: 240, y: 120 },
  target: { x: 420, y: 80 },
  vertices: [{ x: 320, y: 170 }],
  connector: { name: 'rounded' },
  attrs: {
    line: {
      stroke: '#653433',
      sourceMarker: { ...marker },
      targetMarker: { ...marker },
    },
  },
})

