  var target = graph.addNode({
    x: 400,
    y: 260,
    width: 100,
    height: 40,
    attrs: {
      body: {
        fill: '#f5f5f5',
        stroke: '#d9d9d9'
      }
    }
  });
  var obstacles = [];
  var obstacle = graph.addNode({
    x: 240,
    y: 120,
    width: 80,
    height: 30,
    attrs: {
      body: {
        fill: '#ffd591',
        stroke: '#ffa940'
      }
    }
  });
  var edge;

  var update = function update() {
    var edgeView = graph.findViewByCell(edge);
    edgeView.update();
  };

  obstacles.push(obstacle);
  obstacles.push(graph.addNode(obstacle.clone().translate(100, 80)));
  obstacles.push(graph.addNode(obstacle.clone().translate(-100, 140)));
  obstacles.forEach(function (obstacle) {
    return obstacle.on('change:position', update);
  });
  edge = graph.addEdge({
    source: source,
    target: target,
    router: {
      name: 'metro',
      args: {
        startDirections: ['bottom'],
        endDirections: ['top']
      }
    },
    attrs: {
      line: {
        stroke: '#722ed1'
      }
    }
  });
});

