        group: 'group1',
        // 通过 args 指定绝对位置
        args: {
          x: '100%',
          y: '100%'
        },
        attrs: {
          text: {
            text: "{ x: '100%', y: '100%' }"
          }
        },
        label: {
          position: {
            name: 'right'
          }
        }
      }]
    }
  });
});

