  y: 260,
  width: 100,
  height: 40,
  attrs: {
    body: {
      fill: '#f5f5f5',
      stroke: '#d9d9d9',
    },
  },
})

const obstacles: Node[] = []
const obstacle = graph.addNode({
  x: 240,
  y: 120,
  width: 80,
  height: 30,
  attrs: {
    body: {
      fill: '#ffd591',
      stroke: '#ffa940',
