import { ForceLayout } from '@antv/layout'

const graph = new Graph({
  container: document.getElementById('container')!,
  grid: true,
})

const originData = {
  nodes: [
    { id: 'node0', size: 50, x: 0, y: 0 },
    { id: 'node1', size: 30, x: 0, y: 0 },
    { id: 'node2', size: 30, x: 0, y: 0 },
    { id: 'node3', size: 30, x: 0, y: 0 },
